pragma solidity >=0.4.21 <0.7.0;

contract E_loto {
    constructor() public {}

    bytes1 A = 0x41;
    bytes1 B = 0x42;
    bytes1 C = 0x43;
    bytes1 D = 0x44;
    bytes1 E = 0x45;
    bytes1 F = 0x46;
    bytes1 zero = 0x30;
    bytes1 one = 0x31;
    bytes1 two = 0x32;
    bytes1 three = 0x33;
    bytes1 four = 0x34;
    bytes1 five = 0x35;
    bytes1 six = 0x36;
    bytes1 seven = 0x37;
    bytes1 eight = 0x38;
    bytes1 nine = 0x39;

    struct Game {
        bool isActive;
        uint256 counter;
        uint256 stakesTotal;
        mapping(address => uint256) stakes;
        mapping(address => bytes1) bets;
    }

    mapping(bytes32 => Game) public games;

    modifier onlyValidBet(bytes1 _bet) {
        require(
            _bet == zero ||
                _bet == one ||
                _bet == two ||
                _bet == three ||
                _bet == four ||
                _bet == five ||
                _bet == six ||
                _bet == seven ||
                _bet == eight ||
                _bet == nine ||
                _bet == A ||
                _bet == C ||
                _bet == D ||
                _bet == E ||
                _bet == F,
            "Bet is'n valid"
        );
        _;
    }

    function placeStake(bytes1 _bet, bytes32 _gameId)
        public
        payable
        onlyValidBet(_bet)
    {
        Game storage currentGame = games[_gameId];

        // require(currentGame.isActive, "Game id doesn't exist");
        require(currentGame.counter < 10, "No stakes available");
        require(msg.value == 0.015 ether, "Stake must be equal 0.015 ether");
        require(
            currentGame.stakes[msg.sender] == 0,
            "You have already placed your bet"
        );

        currentGame.stakes[msg.sender] = msg.value;
        currentGame.bets[msg.sender] = _bet;
        currentGame.counter = currentGame.counter + 1;
        currentGame.stakesTotal = currentGame.stakesTotal + msg.value;
    }

    function getStake(bytes1 _gameId) public view returns (bytes1) {
        Game storage currentGame = games[_gameId];
        return currentGame.bets[msg.sender];
    }
}
